        <script type="text/javascript">$(function() {
                $('#PleaseWrite').click(function() {
                    $('.tan_boxThree').show();
                    $('.black_ceng').show();
                }) $('.span_no').click(function() {
                    $('.tan_boxThree').hide();
                    $('.black_ceng').hide();
                })
            }) function zhao_checkMessage(obj) {
                if (obj.attr('doing') == 1) {
                    alert('正在提交中。。请稍后');
                    return false;
                }
                var name = $("#z_name").val();
                var content = $("#z_content").val();
                var phone = $("#z_phone").val();
                var target_id = $("#y_target_id").val();
                var origin = $("#origin").val();
                var MCeng_lasturl_1 = $("#Ceng_lasturl_1").val();
                var MCeng_fromurl_1 = $("#Ceng_fromurl_1").val();
                var regexp = /^([a-zA-Z0-9]|[\u4e00-\u9fa5]|【){1}([a-zA-Z0-9]|\s|\n|-|,|\.|。|\！|!|，|\?|？|【|】|—|[\u4e00-\u9fa5]){2,199}$/;
                if (content == '填写意向需求') {
                    content = '';
                }
                if (name == '') {
                    alert('姓名不能为空！');
                    return false;
                } else if (phone == '' || phone == '如 13111111111') {
                    alert('联系方式不能为空！');
                    return false;
                } else if (!phone.match(/^(13[0-9]|14[0-9]|15([0-9])|18([0-9])|17([0-9]))[0-9]{8}$/)) {
                    alert('请输入正确的手机号码！');
                    return false;
                } else if (!regexp.test(content)) {
                    alert('项目名称格式不正确！');
                    return false;
                } else {
                    obj.attr('doing', 1);
                    $.ajax({
                        async: false,
                        type: 'get',
                        url: 'http://liuyan.jmw.com.cn/message/first_message.php',
                        dataType: 'jsonp',
                        jsonp: 'callback',
                        data: {
                            is_login: is_login,
                            name: name,
                            telephone: phone,
                            message: content,
                            target_id: target_id,
                            origin: origin,
                            MFoot_lasturl: MCeng_lasturl_1,
                            MFoot_fromurl: MCeng_fromurl_1
                        },
                        success: function(html) {
                            obj.attr('doing', 0);
                            if (html.status == 'login') {
                                $('.tan_boxThree').hide();
                                alert('留言成功！');
                                window.location.reload();
                            } else if (html.status == 'unlogin') {
                                $('#first_message_id').val(html.id);
                            }
                        }
                    });
                    if (is_login == 'unlogin') {
                        $('.tan_boxThree').hide();
                        $('#Pline_Message').show();
                        $('#first_telephone').val(phone);
                        Auto_trigger();
                    }
                }
            }</script>
