                            code = ""; //清除验证码。如果不清除，过时间后，输入收到的验证码依然有效    
                        } else {
                            curCount--;
                            $("#chBtn").attr("disabled", "false");
                            $("#chBtn").val("" + curCount + "秒");
                        }
                    }
                    InterValObj = window.setInterval(SetRemainTime, 1000); //启动计时器，1秒执行一次
                }</script>
